#!/bin/bash

# Start the app using the docker image
sudo docker run --platform linux/arm64 -d -v inventory-app:/public -p "5009":"5009" -e PORT="5009" -e CONNECTION_STRING="$(aws ssm get-parameter --name "/inventory-app/env/connection-string" --query "Parameter.Value" --output text)" -e JWT_SECRET="$(aws ssm get-parameter --name "/inventory-app/env/JWT_SECRET" --query "Parameter.Value" --output text)" -e ACCESS_TOKEN_SECRET="$(aws ssm get-parameter --name "/inventory-app/env/ACCESS_TOKEN_SECRET" --query "Parameter.Value" --output text)" -e REFRESH_TOKEN_SECRET="$(aws ssm get-parameter --name "/inventory-app/env/REFRESH_TOKEN_SECRET" --query "Parameter.Value" --output text)" -e SESSION_TTL="$(aws ssm get-parameter --name "/inventory-app/env/SESSION-TTL" --query "Parameter.Value" --output text)" -e ALLOWED_ORIGINS="$(aws ssm get-parameter --name "/inventory-app/env/ALLOWED_ORIGINS" --query "Parameter.Value" --output text)" --name "$(aws ssm get-parameter --name "/inventory-app/images/name" --query "Parameter.Value" --output text)-v2" "$(aws ssm get-parameter --name "docker-image-name-inventory-app" --query "Parameter.Value" --output text)amd-arm-v2"